        <!-- POPUP with Wordpress API-->
        <script type="text/javascript" async>
           // On essaie d'être compatible avec le plus de navigateurs
           var eventMethod = window.addEventListener ? "addEventListener" : "attachEvent";
           var eventer = window[eventMethod];
           var messageEvent = eventMethod == "attachEvent" ? "onmessage" : "message";
           // On écoute les messages envoyés par la frame invitée
           eventer(messageEvent,function(e) {
               if(document.getElementsByClassName("ember-modal-dialog")[0] != null) {
                   document.getElementsByClassName("ember-modal-dialog")[0].style.height = (e.data+120) + "px";
               }
           },false);
        </script>

        <!-- Check new version every hour -->
        <script type="text/javascript" >
            // Initialisation
            var version = localStorage.getItem('app-version');
            if (!version) {
                $.ajax({
                  type: 'GET',
                  url: '/api/console/version.json',
                  dataType: 'json'
                }).done(function (data) {
                   localStorage.setItem('app-version', data.version);
                }).fail(function(e) {
                  console.error("Error getting version")
                });
            }

            // Periodic check
            setInterval(function() {
                $.ajax({
                  type: 'GET',
                  url: '/api/console/version.json',
                  dataType: 'json'
                }).done(function (data) {
                  if (version !== data.version) {
                    localStorage.setItem('app-version', data.version);
                    // console.log("Reloading")
                    document.location.reload(true);
                  }
                }).fail(function(e) {
                  console.error("Error getting version")
                });
            }, 3600000);
        </script>

        <!-- Compatibilité IE -->
        <script type="text/javascript" >
            if (!Object.values) {
                Object.values = function (obj) {
                    var res = [];
                    for (var i in obj) {
                        if (obj.hasOwnProperty(i)) {
                            res.push(obj[i]);
                        }
                    }
                    return res;
                }
            }

            if (typeof NodeList !== "undefined" && NodeList.prototype && !NodeList.prototype.forEach) {
                NodeList.prototype.forEach = Array.prototype.forEach;
            }
        </script>


