@media (hover: hover) and (pointer: fine) {
	:root {
		--brighten: -1;
	}

	a[href]:hover,
	button:not(:disabled):hover,
	input[type="button"]:not(:disabled):hover,
	input[type="image"]:not(:disabled):hover,
	input[type="reset"]:not(:disabled):hover,
	input[type="submit"]:not(:disabled):hover {
		background-color: var(--hover-tint);
	}

	a[href]:active,
	button:not(:disabled):active,
	input[type="button"]:not(:disabled):active,
	input[type="image"]:not(:disabled):active,
	input[type="reset"]:not(:disabled):active,
	input[type="submit"]:not(:disabled):active {
		background-color: var(--active-tint);
	}

	a[href]:active,
	a[href]:hover {
		text-decoration-thickness: 0.125em;
	}

	.fr-enlarge-link:hover {
		background-color: var(--hover);
	}

	.fr-enlarge-link:active {
		background-color: var(--active);
	}

	.fr-header__brand.fr-enlarge-link a[href]:hover {
		--a: 0.1;
	}

	.fr-header__brand.fr-enlarge-link a[href]:active {
		--a: 0.2;
	}
}

@media (-ms-high-contrast: active) and (-ms-high-contrast: active),
(-ms-high-contrast: none) and (-ms-high-contrast: none),
all and (-ms-high-contrast: active) and (-ms-high-contrast: none),
all and (-ms-high-contrast: none) and (-ms-high-contrast: active) {
	.fr-enlarge-link {
		background-color: transparent;
	}

	.fr-enlarge-link:hover {
		background-color: rgba(0, 0, 0, 0.05);
	}

	.fr-enlarge-link:active {
		background-color: rgba(0, 0, 0, 0.1);
	}
}

@media (-ms-high-contrast: active),
(-ms-high-contrast: none) {
