:root a {
	text-decoration-line: underline;
}

:focus,
:focus-visible {
	z-index: 500;
}

[tabindex],
a {
	outline-color: #0a76f6;
	outline-offset: 2px;
	outline-width: 2px;
}

[tabindex]:focus,
a:focus {
	outline-style: solid;
}

[contenteditable]:not([contenteditable="false"]):focus:not(:focus-visible),
[tabindex]:focus:not(:focus-visible),
a:focus:not(:focus-visible),
audio:focus:not(:focus-visible)[controls],
button:focus:not(:focus-visible),
details:focus:not(:focus-visible),
details>summary:first-of-type:focus:not(:focus-visible),
input:focus:not(:focus-visible),
input[type="button"]:focus:not(:focus-visible),
input[type="checkbox"]:focus:not(:focus-visible)+label:before,
input[type="image"]:focus:not(:focus-visible),
input[type="radio"]:focus:not(:focus-visible)+label:before,
input[type="reset"]:focus:not(:focus-visible),
input[type="submit"]:focus:not(:focus-visible),
select:focus:not(:focus-visible),
textarea:focus:not(:focus-visible),
video:focus:not(:focus-visible)[controls] {
	outline-style: none;
}

[contenteditable]:not([contenteditable="false"]):focus-visible,
[tabindex]:focus-visible,
a:focus-visible,
audio:focus-visible[controls],
button:focus-visible,
details:focus-visible,
details>summary:first-of-type:focus-visible,
input:focus-visible,
input[type="button"]:focus-visible,
input[type="checkbox"]:focus-visible+label:before,
input[type="image"]:focus-visible,
input[type="radio"]:focus-visible+label:before,
input[type="reset"]:focus-visible,
input[type="submit"]:focus-visible,
select:focus-visible,
textarea:focus-visible,
video:focus-visible[controls] {
	outline-style: solid;
}

a {
	cursor: pointer;
}

a:not([href]) {
	cursor: not-allowed;
}

a:not([href]) {
	color: var(--text-disabled-grey);
	opacity: 1;
}

.fr-enlarge-link {
	position: relative;
}

.fr-enlarge-link a {
	--link-blank-content: none;
	outline-width: 0 !important;
}

.fr-enlarge-link a {
	text-decoration-line: none;
}

.fr-enlarge-link a:before {
	bottom: 0;
	content: "";
	display: block;
	height: 100%;
	left: 0;
	outline-color: inherit;
	outline-offset: 2px;
	outline-style: inherit;
	outline-width: 2px;
	position: absolute;
	right: 0;
	top: 0;
	width: 100%;
	z-index: 1;
}

