.fr-radio-rich input[type="radio"]:not(:disabled)~label:active~.fr-radio-rich__img {
	--brightness: calc(100% + var(--brighten) * 20%);
}

.fr-checkbox-group input[type="checkbox"]:active:not(:disabled)+label:before {
	--hover: var(--background-active-blue-france-hover);
	--active: var(--background-active-blue-france-active);
	--data-uri-svg: url("data:image/svg+xml;charset=utf8,<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24'><path fill='%23fff' d='M10 15.17l9.2-9.2 1.4 1.42L10 18l-6.36-6.36 1.4-1.42z'/></svg>");
	background-color: var(--background-active-blue-france);
	background-image: var(--data-uri-svg);
