'use strict';
module.exports = function(grunt) {
    require('load-grunt-tasks')(grunt);

    grunt.initConfig({
        cssmin: {
            target: {
                files: {
                    'css/min.css': [ 'plugins/lightbox/css/lightbox.css', 'plugins/flexslider/flexslider.css' ,'plugins/leaflet-0.7.3/leaflet.css', 'css/sass/style.css']
                }
            }
        },
        sass: {
            dist: {
                files: [{
                    expand: true,
                    cwd: '.',
                    src: ['sass/*.scss'],
                    dest: 'css',
                    ext: '.css'
                }]
            }
        },
        watch: {
            css: {
                files: ['sass/*.scss', '!css/min.*'],
                tasks: ['sass' ,'cssmin'],
                options: {
                    spawn: false,
                },
            },
        },
    });

    // Load tasks
    grunt.loadNpmTasks('grunt-contrib-sass');

    // Register tasks
    grunt.registerTask('default', [
        'watch',
    ]);
};

