function isScrolledIntoView(elem) {
                var docViewTop = $(window).scrollTop();
                var docViewBottom = docViewTop + $(window).height();

                var elemTop = $(elem).offset().top;
                var elemBottom = elemTop + $(elem).height();

                return ((elemBottom <= docViewBottom) && (elemTop >= docViewTop));
            }

            function getBreadcrumbElement(position) {
                var breadcrumb = $('.breadcrumb').text().replace(/\t|\n/g, '').split($('.breadcrumb .navigation-pipe').first().text().replace(/\t|\n/g, '').trim());
                if (breadcrumb.length > position) return breadcrumb[position].toLowerCase().atiFormat();
                return '';
            }

            function getCurrentFormat() {
                var form = $('#buy_block');
                if (form.length == 0) return '';
                var format = '';
                form.serializeArray().forEach(function (input) {
                    if (input.name.startsWith('group_')) {
                        var id_attribute = input.value;
                        if (id_attribute == 44) {
                            format = 'electronique';
                        } else if (id_attribute == 43) {
                            format = 'impression';
                        } else return;
                    }
                })
                return format;
            }

            function getNoteMoyenne() {
                var note = false;
                const comments = $('#product_comments_block_tab');
                if (comments.length > 0) {
                    note = 0;
                    comments.find('.comment').each(function () {
                        const itemprop = $(this).find('meta[itemprop="ratingValue"]');
                        if (itemprop.length > 0) note += parseInt(itemprop.attr('content'));
                    });
                }
                return note;
            }

