                    for (var t in types) {
                        $this.on(types[t], selector, data, fn);

                        var currentBindings = $._data(this, 'events')[types[t]];
                        if ($.isArray(currentBindings)) {
                            currentBindings.unshift(currentBindings.pop());
                        }
                    }


                });
                return this;
            };

