            function getCartId(retry) {
                return new Promise((resolve, reject) => {
                    $.ajax({
                        type: 'POST',
                        headers: {"cache-control": "no-cache"},
                        url: (typeof (baseUri) !== 'undefined') ? baseUri + '?rand=' + new Date().getTime() : '',
                        async: true,
                        cache: false,
                        dataType: "json",
                        data: (typeof (static_token) !== 'undefined') ? 'controller=cart&ajax=true&token=' + static_token : '',
                        success: ((jsonData) => {
                            if (jsonData) {
                                if (jsonData.cartId !== '') {
                                    resolve(parseInt(jsonData.cartId));
                                } else {
                                    retry = retry ? retry + 1 : 1;
                                    if (retry > 5) {
                                        reject('Too much retry on getCartId');
                                    } else {
                                        setTimeout(resolve(getCartId(retry)), 1000);
                                    }
                                }
                            }
                        })
                    });
                });
            }
